import React from "react";
import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";

const Cost = (props) => {
  const budget = props.budget;
  const total = props.total;

  const remaining = budget - total;

  const items = props.item.map((item, index) => (
    <li key={index}>
      {" "}
      {item.name} - {item.price}{" "}
    </li>
  ));

  function isOverBudget(remainingBudget) {
    if (remainingBudget < 0) {
      return true;
    }
    return false;
  }

  let overBudget = isOverBudget(remaining);

  return (
    <div>
      <Container
        style={{
          backgroundColor: "lightgrey",
          alignitems: "center",
          textAlign: "left",
          fontWeight: "bold",
          color: "black",
        }}
      >
        <Card
          style={{
            backgroundColor: "lightgrey",
            alignitems: "center",
            textAlign: "left",
            fontWeight: "bold",
            color: "black",
          }}
        >
          <Card.Body>
            <ul>
              <span> Budget: {budget} </span> <br />
              <span> Remaining Budget: {remaining} </span> <br />
              <span> {items}</span> <br />
              <span> Total Cost: {total} </span>
              <br />
            </ul>
            <div> {overBudget} </div>
            {/* <div className ="container" {if (overBudget) { 'msg' }}>Hello World!</div> */}
          </Card.Body>
        </Card>
      </Container>
    </div>
  );
};

export default Cost;

