import React from "react";
import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";

const Cost = (props) => {
  const budget = props.budget;
  const total = props.total;

  const remaining = budget - total;

  const items = props.item.map((item, index) => (
    <li
      style={{
        textTransform: "capitalize",
        fontStyle: "italic",
        textDecoration: "underline",
        fontSize: "18px",
      }}
      key={index}
    >
      {item.name}: {item.price}
    </li>
  ));

  function isOverBudget(remainingBudget) {
    if (remainingBudget < 0) {
      return true;
    }
    return false;
  }

  let overBudget = isOverBudget(remaining);

  return (
    <div>
      <Container
        style={{
          backgroundColor: "lightgrey",
          alignitems: "center",
          textAlign: "left",
          fontWeight: "bold",
          color: "black",
        }}
      >
        <Card
          style={{
            backgroundColor: "lightgrey",
            alignitems: "center",
            textAlign: "left",
            fontWeight: "bold",
            color: "black",
            border: "none",
          }}
        >
          <Card.Body>
            <ul style={{ listStyle: "none", fontSize: "20px" }}>
              <span> Budget: {budget} </span> <br />
              <span style={overBudget ? exampleStyle1 : exampleStyle2}>
                Remaining Budget: {remaining}
              </span>
              <br /> <br />
              <span> Item & Cost:{items}</span> <br />
              <span> Total Cost: {total} </span>
              <br />
            </ul>
          </Card.Body>
        </Card>
      </Container>
    </div>
  );
};

const exampleStyle1 = { color: "red" };
const exampleStyle2 = { color: "black" };

export default Cost;

