import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";

class FormInput extends Component {
  state = {};
  render() {
    return (
      <React.Fragment>
        <Card>
          <Card.Header> Featured </Card.Header>
          <Card.Body>
            <Card.Title> Title </Card.Title>
            <Card.Text> Text </Card.Text>
          </Card.Body>
        </Card>
        <Container style={{ flex: 0.1, backgroundColor: "lightgrey" }}>
          <Form>
            <Form.Group controlId="budget">
              <Form.Label>Budget</Form.Label>
              <Form.Control type="number" placeholder="Enter Budget" />
              <Form.Text className="text-muted">
                Input monthly budget here!
              </Form.Text>
            </Form.Group>

            <Form.Group controlId="itemName">
              <Form.Label>Item</Form.Label>
              <Form.Control type="text" placeholder="Input items" />
            </Form.Group>

            <Form.Group controlId="itemCost">
              <Form.Label>Item Cost</Form.Label>
              <Form.Control type="number" placeholder="Input items cost" />
            </Form.Group>

            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>{" "}
        </Container>
      </React.Fragment>
    );
  }
}

export default FormInput;
