import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";
import Cost from "./cost";

class FormInput extends Component {
  constructor(props) {
    super(props);
    this.state = { budget: 0, items: [] };
  }

  handleSubmit = (event) => {
    event.preventDefault();
    const item = [
      { name: event.target.itemName.value, price: event.target.itemCost.value },
    ];
    const budget = { budget: event.target.budget.value };
    this.setState({ budget, items: [...this.state.items, ...item] });
    console.log(this.state);
  };
  // when handle submit, would execute chain of promises - first one is want state to be updated - next is add items to list and calculate budget diff -
  handleInputChange = (event) => {
    //   this.setState({
    //     [event.target.name]: event.target.value,
    // });
    //   // console.log(event);
    //   // console.log(event.target.name);
    //   // console.log(event.target.value);
    //   // console.log(this.state);
  };

  render() {
    // const { budget, items } = this.state;

    return (
      <React.Fragment>
        <Card className="bg-light container border-0">
          <Card.Header
            className=" bg-light container border-0"
            as="h1"
            style={{
              alignitems: "center",
              textAlign: "center",
              color: "darkgrey",
            }}
          >
            {" "}
            BUDGET APP{" "}
          </Card.Header>
          <Card.Body>
            <Card.Title
              style={{
                alignitems: "center",
                textAlign: "center",
                fontWeight: "bold",
                color: "blue",
              }}
            >
              {" "}
              Calculate Your Budget{" "}
            </Card.Title>

            <Container
              className="rounded-lg"
              style={{ backgroundColor: "lightgrey" }}
            >
              {/* <p> Budget is {budget} </p>
              <p> Item is {itemName} </p>
              <p> Cost is {itemCost} </p> */}
              <Form onSubmit={this.handleSubmit} className="p-3">
                <Form.Group
                  onChange={this.handleInputChange}
                  controlId="budget"
                >
                  <Form.Label className="bold ">Budget</Form.Label>
                  <Form.Control
                    type="number"
                    name="budget"
                    value={this.state.value}
                    placeholder="Enter Budget"
                  />
                </Form.Group>

                <Form.Group
                  onChange={this.handleInputChange}
                  controlId="itemName"
                >
                  <Form.Label>Item</Form.Label>
                  <Form.Control
                    type="text"
                    name="itemName"
                    value={this.state.value}
                    placeholder="Input items"
                  />
                </Form.Group>

                <Form.Group
                  onChange={this.handleInputChange}
                  controlId="itemCost"
                >
                  <Form.Label>Item Cost</Form.Label>
                  <Form.Control
                    type="number"
                    name="itemCost"
                    value={this.state.value}
                    placeholder="Input items cost"
                  />
                </Form.Group>

                <Button variant="primary" type="submit">
                  Submit
                </Button>
              </Form>{" "}
              {/* <Cost items={this.state.items} budget={this.state.budget}>
                {" "}
                John{" "} */}
              <Cost budget={this.state.budget}> </Cost>
            </Container>
          </Card.Body>
        </Card>
      </React.Fragment>
    );
  }
}

export default FormInput;
