import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";

class FormInput extends Component {
  state = {};
  render() {
    return (
      <React.Fragment>
        <Card className="bg-light container border-0">
          <Card.Header
            className=" bg-light container border-0"
            as="h1"
            style={{
              alignitems: "center",
              textAlign: "center",
              color: "darkgrey",
            }}
          >
            {" "}
            BUDGET APP{" "}
          </Card.Header>
          <Card.Body>
            <Card.Title
              style={{
                alignitems: "center",
                textAlign: "center",
                fontWeight: "bold",
                color: "blue",
              }}
            >
              {" "}
              Calculate Your Budget{" "}
            </Card.Title>

            <Container
              className="rounded-lg"
              style={{ backgroundColor: "lightgrey" }}
            >
              <Form className="p-3">
                <Form.Group controlId="budget">
                  <Form.Label className="bold underline">Budget</Form.Label>
                  <Form.Control type="number" placeholder="Enter Budget" />
                  <Form.Text className="text-muted">
                    Input monthly budget here!
                  </Form.Text>
                </Form.Group>

                <Form.Group controlId="itemName">
                  <Form.Label>Item</Form.Label>
                  <Form.Control type="text" placeholder="Input items" />
                </Form.Group>

                <Form.Group controlId="itemCost">
                  <Form.Label>Item Cost</Form.Label>
                  <Form.Control type="number" placeholder="Input items cost" />
                </Form.Group>

                <Button variant="primary" type="submit">
                  Submit
                </Button>
              </Form>{" "}
            </Container>
          </Card.Body>
        </Card>
      </React.Fragment>
    );
  }
}

export default FormInput;
