import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";
import Cost from "./cost";

class FormInput extends Component {
  constructor(props) {
    super(props);
    this.state = { budget: 0, items: [], total: 0 };
  }

  handleSubmit = (event, sumOfTotals) => {
    event.preventDefault();
    // console.log(this.state.items.name);
    const item = [
      { name: event.target.itemName.value, price: event.target.itemCost.value },
    ];
    // const budget = { budget: event.target.budget.value };
    // const total = {
    //   total: parseInt(this.state.total) + parseInt(event.target.itemCost.value),
    // };
    this.setState({
      budget: event.target.budget.value,
      items: [...this.state.items, ...item],
      total: this.state.total + parseInt(event.target.itemCost.value),
    });
    console.log(this.state);
  };

  // sumOfTotals = () => {
  //   // const onlyItem = [{ price: event.target.itemCost.value }];
  //   const item = [{ price: event.target.itemCost.value }];
  //   console.log(item);
  //   if (item > 0) {
  //     console.log(item);
  //     return item.reduce((prev, current) => prev.item + current.item);
  //   } else {
  //     return false;
  //   }
  // };

  // when handle submit, would execute chain of promises - first one is want state to be updated - next is add items to list and calculate budget diff -

  render() {
    const budget = this.state.budget;
    const tester = Object.values(budget);
    // console.log(tester);
    return (
      <React.Fragment>
        <Card className="bg-light container border-0" style={{ height: 90 }}>
          <Card.Header
            className=" bg-light container border-0"
            as="h1"
            style={{
              alignitems: "center",
              textAlign: "center",
              color: "darkgrey",
            }}
          >
            {" "}
            BUDGET APP{" "}
          </Card.Header>
          <Card.Body>
            <Card.Title
              style={{
                alignitems: "center",
                textAlign: "center",
                fontWeight: "bold",
                color: "blue",
              }}
            >
              {" "}
              Calculate Your Budget{" "}
            </Card.Title>

            <Container
              className="rounded-lg"
              style={{ backgroundColor: "lightgrey" }}
            >
              {/* <p> Budget is {budget} </p> */}
              {/* <p> Budget is {budget} </p>
              <p> Item is {itemName} </p>
              <p> Cost is {itemCost} </p> */}
              <Form onSubmit={this.handleSubmit} className="p-3">
                <Form.Group
                  onChange={this.handleInputChange}
                  controlId="budget"
                >
                  <Form.Label className="bold ">Budget</Form.Label>
                  <Form.Control
                    type="number"
                    name="budget"
                    value={this.state.value}
                    placeholder="Enter Budget"
                  />
                </Form.Group>

                <Form.Group
                  onChange={this.handleInputChange}
                  controlId="itemName"
                >
                  <Form.Label>Item</Form.Label>
                  <Form.Control
                    type="text"
                    name="itemName"
                    value={this.state.value}
                    placeholder="Input items"
                  />
                </Form.Group>

                <Form.Group onChange={this.sumOfTotals} controlId="itemCost">
                  <Form.Label>Item Cost</Form.Label>
                  <Form.Control
                    type="number"
                    name="itemCost"
                    value={this.state.value}
                    placeholder="Input items cost"
                  />
                </Form.Group>

                <Button variant="primary" type="submit">
                  Submit
                </Button>
              </Form>{" "}
              {/* <Cost items={this.state.items} budget={this.state.budget}>
                {" "}
                John{" "} */}
              <Cost
                budget={this.state.budget}
                item={this.state.items}
                total={this.state.total}
              >
                {" "}
              </Cost>
            </Container>
          </Card.Body>
        </Card>
      </React.Fragment>
    );
  }
}
// const budget = this.state.budget;
// const newTest = () => Object.entries(budget);
// console.log(newTest);

// const tester = Object.keys(budget); //which means this doesnt work
// console.log(tester);
export default FormInput;
