import Card from "react-bootstrap/Card";

class FormInput extends Component {
  // state = { budget: 0, itemName: "", itemCost: 0 };

  state = {
    items: [{ budget: 2, itemName: "cat", itemCost: 12 }],
    tags: [1, 2, 3],
  };

