  //   this.setState(
  //     [ { budget: event.budget },
  //       { itemName: event.itemName },
  //       { itemCost: event.itemCost },
  //     ]
  //   // console.log(this.state);
  // };

  // handleSubmit = (event) => {
  //   event.preventDefault();
  //   this.setState({
  //     budget: event.budget,
  //     itemName: event.itemName,
  //     itemCost: event.itemCost,
  //   });
